hopp.test('Should successfully parse JSONC with multiline comments', () => {
  hopp.expect(hopp.response.statusCode).toBe(200);
  const data = JSON.parse(hopp.response.body.asJSON().data);
  hopp.expect(data.message).toBe('test');
  hopp.expect(data.nested.field).toBe('value');
});

hopp.test('Should successfully parse JSONC with trailing commas', () => {
  hopp.expect(hopp.response.statusCode).toBe(200);
  const data = JSON.parse(hopp.response.body.asJSON().data);
  hopp.expect(data.key).toBe('value');
  hopp.expect(data.count).toBe(42);
});
