requests.testScript:
// Check status code is 200
pw.test("Status code is 200", ()=> {
    pw.expect(pw.response.status).toBe(200);
});

// Check JSON response property
pw.test("Check JSON response property", ()=> {
    pw.expect(pw.response.body.method).toBe("GET");
    pw.expect(pw.response.body.headers).toBeType("object");
 }); 

// Check Content-Type
 pw.test("Check Content-Type", () => {
  let contentType = null;
  for (const header of pw.response.headers) {
  if (header.key.toLowerCase() === 'content-type') {
    contentType = header.value;
    break;
  }
}
pw.expect(contentType).toBe('application');
 });
