requests.preRequestScript:
export {};
hopp.request.setUrl('https://echo.hoppscotch.io/modified')
hopp.request.setMethod('POST')
hopp.request.setHeader('Modified-Header', 'modified_value')
hopp.request.setParam('new_param', 'new_value')

hopp.request.setBody({
  contentType: 'application/json',
  body: JSON.stringify({ modified: true, timestamp: Date.now() })
})

hopp.request.setAuth({
  authType: 'bearer',
  token: 'test-bearer-token',
  authActive: true
})

requests.testScript:
export {};

hopp.test('Request URL was modified by pre-request script', () => {
    hopp.expect(hopp.request.url).toInclude('/modified')
    pm.expect(pm.request.url.toString()).toInclude('/modified')
})

hopp.test('Request method was modified by pre-request script', () => {
    hopp.expect(hopp.request.method).toBe('POST')
    pm.expect(pm.request.method).toBe('POST')
})

hopp.test('Request headers contain both original and modified headers', () => {
    const headers = hopp.request.headers
    const hasOriginal = headers.some(h => h.key === 'Original-Header')
    const hasModified = headers.some(h => h.key === 'Modified-Header')
    hopp.expect(hasOriginal).toBe(true)
    hopp.expect(hasModified).toBe(true)
})

pm.test('PM request headers can be accessed and checked', () => {
    pm.expect(pm.request.headers.has('Original-Header')).toBe(true)
    pm.expect(pm.request.headers.has('Modified-Header')).toBe(true)
    pm.expect(pm.request.headers.get('Modified-Header')).toBe('modified_value')
})

hopp.test('Request parameters contain both original and new parameters', () => {
    const params = hopp.request.params
    const hasOriginal = params.some(p => p.key === 'original_param')
    const hasNew = params.some(p => p.key === 'new_param')
    hopp.expect(hasOriginal).toBe(true)
    hopp.expect(hasNew).toBe(true)
})

hopp.test('Request body was modified by pre-request script', () => {
    hopp.expect(hopp.request.body.contentType).toBe('application/json')
    pm.expect(pm.request.body.contentType).toBe('application/json')
    const bodyData = hopp.request.body

    if (typeof bodyData.body === "string") {
        hopp.expect(JSON.parse(bodyData.body).modified).toBe(true)
        pm.expect(JSON.parse(bodyData.body).modified).toBe(true)
    } else {
        throw new Error(`Unexpected body type: ${bodyData.body}`)
    }
})


hopp.test('Request auth was modified by pre-request script', () => {
    const auth = hopp.request.auth

    if (auth.authType === 'bearer') {
        hopp.expect(auth.token).toBe('test-bearer-token')
        pm.expect(auth.token).toBe('test-bearer-token')
    } else {
        throw new Error(`Unexpected auth type: ${auth.authType}`)
    }

    hopp.expect(auth.token).toBe('test-bearer-token')
    pm.expect(auth.token).toBe('test-bearer-token')
})

