    hopp.expect(parsedData.name).toBe('John Doe')
    hopp.expect(parsedData.age).toBeType("number")
})

pm.test("`pm.response.json()` parses response body as JSON", () => {
    const parsedData = JSON.parse(pm.response.json().data)

    pm.expect(parsedData.name).toBe('John Doe')
    pm.expect(parsedData.age).toBeType("number")
})

