hopp.test('`hopp.env.get()` retrieves environment variables', () => {
    const value = hopp.env.get('test_key')
    hopp.expect(value).toBe('test_value')
})

pm.test('`pm.variables.get()` retrieves environment variables', () => {
    const value = pm.variables.get('test_key')
    pm.expect(value).toBe('test_value')
})

hopp.test('`hopp.env.getRaw()` retrieves raw environment variables without resolution', () => {
    const rawValue = hopp.env.getRaw('recursive_key')
    hopp.expect(rawValue).toBe('<<test_key>>')
})

hopp.test('`hopp.env.get()` resolves recursive environment variables', () => {
    const resolvedValue = hopp.env.get('recursive_key')
    hopp.expect(resolvedValue).toBe('test_value')
})

pm.test('`pm.variables.replaceIn()` resolves template variables', () => {
    const resolved = pm.variables.replaceIn('Value is {{test_key}}')
    pm.expect(resolved).toBe('Value is test_value')
})

hopp.test('`hopp.env.global.get()` retrieves global environment variables', () => {
    const globalValue = hopp.env.global.get('global_key')

    // `hopp.env.global` would be empty for the CLI
    if (globalValue) {
      hopp.expect(globalValue).toBe('global_value')
    }
})

pm.test('`pm.globals.get()` retrieves global environment variables', () => {
    const globalValue = pm.globals.get('global_key')

    // `pm.globals` would be empty for the CLI
    if (globalValue) {
        pm.expect(globalValue).toBe('global_value')
    }
})

hopp.test('`hopp.env.active.get()` retrieves active environment variables', () => {
    const activeValue = hopp.env.active.get('active_key')
    hopp.expect(activeValue).toBe('active_value')
})

pm.test('`pm.environment.get()` retrieves active environment variables', () => {
    const activeValue = pm.environment.get('active_key')
    pm.expect(activeValue).toBe('active_value')
})

hopp.test('Environment methods return null for non-existent keys', () => {
    hopp.expect(hopp.env.get('non_existent')).toBe(null)
    hopp.expect(hopp.env.getRaw('non_existent')).toBe(null)
    hopp.expect(hopp.env.global.get('non_existent')).toBe(null)
    hopp.expect(hopp.env.active.get('non_existent')).toBe(null)
})

pm.test('`pm` environment methods handle non-existent keys correctly', () => {
    pm.expect(pm.variables.get('non_existent')).toBe(undefined)
    pm.expect(pm.environment.get('non_existent')).toBe(undefined)
    pm.expect(pm.globals.get('non_existent')).toBe(undefined)
    pm.expect(pm.variables.has('non_existent')).toBe(false)
    pm.expect(pm.environment.has('non_existent')).toBe(false)
    pm.expect(pm.globals.has('non_existent')).toBe(false)
})

pm.test('`pm` variables set in pre-request script are accessible', () => {
    pm.expect(pm.variables.get('pm_test_key')).toBe('pm_test_value')
    pm.expect(pm.environment.get('pm_active_key')).toBe('pm_active_value')

    const pmGlobalValue = hopp.env.global.get('pm_global_key')

    // `hopp.env.global` would be empty for the CLI
    if (pmGlobalValue) {
      hopp.expect(pmGlobalValue).toBe('pm_global_value')
    }
})

requests.preRequestScript:
export {};
hopp.request.setUrl('https://echo.hoppscotch.io/modified')
hopp.request.setMethod('POST')
hopp.request.setHeader('Modified-Header', 'modified_value')
hopp.request.setParam('new_param', 'new_value')

hopp.request.setBody({
  contentType: 'application/json',
  body: JSON.stringify({ modified: true, timestamp: Date.now() })
})

hopp.request.setAuth({
  authType: 'bearer',
  token: 'test-bearer-token',
  authActive: true
})

requests.testScript:
export {};

hopp.test('Request URL was modified by pre-request script', () => {
    hopp.expect(hopp.request.url).toInclude('/modified')
    pm.expect(pm.request.url.toString()).toInclude('/modified')
})

hopp.test('Request method was modified by pre-request script', () => {
    hopp.expect(hopp.request.method).toBe('POST')
    pm.expect(pm.request.method).toBe('POST')
})

hopp.test('Request headers contain both original and modified headers', () => {
    const headers = hopp.request.headers
    const hasOriginal = headers.some(h => h.key === 'Original-Header')
    const hasModified = headers.some(h => h.key === 'Modified-Header')
    hopp.expect(hasOriginal).toBe(true)
    hopp.expect(hasModified).toBe(true)
})

pm.test('PM request headers can be accessed and checked', () => {
    pm.expect(pm.request.headers.has('Original-Header')).toBe(true)
    pm.expect(pm.request.headers.has('Modified-Header')).toBe(true)
    pm.expect(pm.request.headers.get('Modified-Header')).toBe('modified_value')
})

hopp.test('Request parameters contain both original and new parameters', () => {
    const params = hopp.request.params
    const hasOriginal = params.some(p => p.key === 'original_param')
    const hasNew = params.some(p => p.key === 'new_param')
    hopp.expect(hasOriginal).toBe(true)
    hopp.expect(hasNew).toBe(true)
})

hopp.test('Request body was modified by pre-request script', () => {
    hopp.expect(hopp.request.body.contentType).toBe('application/json')
    pm.expect(pm.request.body.contentType).toBe('application/json')
    const bodyData = hopp.request.body

    if (typeof bodyData.body === "string") {
        hopp.expect(JSON.parse(bodyData.body).modified).toBe(true)
        pm.expect(JSON.parse(bodyData.body).modified).toBe(true)
    } else {
        throw new Error(`Unexpected body type: ${bodyData.body}`)
    }
})


hopp.test('Request auth was modified by pre-request script', () => {
    const auth = hopp.request.auth

    if (auth.authType === 'bearer') {
        hopp.expect(auth.token).toBe('test-bearer-token')
        pm.expect(auth.token).toBe('test-bearer-token')
    } else {
        throw new Error(`Unexpected auth type: ${auth.authType}`)
    }

    hopp.expect(auth.token).toBe('test-bearer-token')
    pm.expect(auth.token).toBe('test-bearer-token')
})

