requests.preRequestScript:
export {};
// Test `pm` namespace specific features
pm.environment.set('pm_pre_key', 'pm_pre_value')
pm.globals.set('pm_global_pre', 'pm_global_pre_value')
pm.variables.set('pm_var_pre', 'pm_var_pre_value')

requests.testScript:
export {};

pm.test('`pm` namespace environment operations work correctly', () => {
    // Test environment has() method
    pm.expect(pm.environment.has('pm_pre_key')).toBe(true)
    pm.expect(pm.environment.has('non_existent_key')).toBe(false)

    // Test globals has() method
    const globalValue = pm.globals.has('pm_global_pre')
    // `pm.globals` would be empty for the CLI
    if (globalValue) {
        pm.expect(pm.globals.has('pm_global_pre')).toBe(true)
    }

    pm.expect(pm.globals.has('non_existent_global')).toBe(false)

    // Test variables has() method
    pm.expect(pm.variables.has('pm_var_pre')).toBe(true)
    pm.expect(pm.variables.has('non_existent_var')).toBe(false)
})

pm.test('`pm` variables.replaceIn() handles template replacement', () => {
    const template = 'Hello {{pm_pre_key}}, global: {{pm_global_pre}}'
    const resolved = pm.variables.replaceIn(template)
    pm.expect(resolved).toInclude('pm_pre_value')
    pm.expect(resolved).toInclude('pm_global_pre_value')
})

pm.test('`pm` request object provides URL as object with toString', () => {
    const url = pm.request.url
    pm.expect(url.toString()).toBeType('string')
    pm.expect(url.toString()).toInclude('echo.hoppscotch.io')
})

pm.test('`pm` request headers object methods work correctly', () => {
    // Test headers.all() returns object
    const allHeaders = pm.request.headers.all()
    pm.expect(allHeaders).toBeType('object')

    // Test headers.has() and headers.get() methods
    if (Object.keys(allHeaders).length > 0) {
        const firstHeaderKey = Object.keys(allHeaders)[0]
        pm.expect(pm.request.headers.has(firstHeaderKey)).toBe(true)
        pm.expect(pm.request.headers.get(firstHeaderKey)).toBeType('string')
    }

    // Test non-existent header
    pm.expect(pm.request.headers.has('non-existent-header')).toBe(false)
    pm.expect(pm.request.headers.get('non-existent-header')).toBe(null)
})

pm.test('`pm` response headers work correctly', () => {
    // Test response headers all() method
    const allResponseHeaders = pm.response.headers.all()
    pm.expect(allResponseHeaders).toBeType('object')

    // Test headers has() and get() for common headers
    if (Object.keys(allResponseHeaders).length > 0) {
        const firstKey = Object.keys(allResponseHeaders)[0]
        pm.expect(pm.response.headers.has(firstKey)).toBe(true)
        pm.expect(pm.response.headers.get(firstKey)).toBeType('string')
    }
})

