export {};

// EQUALITY ASSERTIONS
hopp.test('Chai equality - equal() method', () => {
  hopp.expect(5).to.equal(5)
  hopp.expect('hello').to.equal('hello')
  hopp.expect(true).to.equal(true)
})

hopp.test('Chai equality - eql() for deep equality', () => {
  hopp.expect({ a: 1 }).to.eql({ a: 1 })
  hopp.expect([1, 2, 3]).to.eql([1, 2, 3])
})

hopp.test('Chai equality - negation with .not', () => {
  hopp.expect(5).to.not.equal(10)
  hopp.expect('hello').to.not.equal('world')
})

// TYPE ASSERTIONS
hopp.test('Chai type - .a() and .an() assertions', () => {
  hopp.expect('test').to.be.a('string')
  hopp.expect(42).to.be.a('number')
  hopp.expect([]).to.be.an('array')
  hopp.expect({}).to.be.an('object')
})

hopp.test('Chai type - instanceof assertions', () => {
  hopp.expect([1, 2, 3]).to.be.instanceof(Array)
  hopp.expect(new Date()).to.be.instanceof(Date)
  hopp.expect(new Error('test')).to.be.instanceof(Error)
})

// TRUTHINESS ASSERTIONS
hopp.test('Chai truthiness - .true, .false, .null, .undefined', () => {
  hopp.expect(true).to.be.true
  hopp.expect(false).to.be.false
  hopp.expect(null).to.be.null
  hopp.expect(undefined).to.be.undefined
})

hopp.test('Chai truthiness - .ok and .exist', () => {
  hopp.expect(1).to.be.ok
  hopp.expect('string').to.exist
  hopp.expect(0).to.not.be.ok
})

hopp.test('Chai truthiness - .NaN assertion', () => {
  hopp.expect(NaN).to.be.NaN
  hopp.expect(42).to.not.be.NaN
})

// NUMERICAL COMPARISONS
hopp.test('Chai numbers - .above() and .below()', () => {
  hopp.expect(10).to.be.above(5)
  hopp.expect(5).to.be.below(10)
  hopp.expect(5).to.not.be.above(10)
})

hopp.test('Chai numbers - aliases gt, lt, gte, lte', () => {
  hopp.expect(10).to.be.gt(5)
  hopp.expect(5).to.be.lt(10)
  hopp.expect(5).to.be.gte(5)
  hopp.expect(5).to.be.lte(5)
})

hopp.test('Chai numbers - .least() and .most()', () => {
  hopp.expect(10).to.be.at.least(10)
  hopp.expect(10).to.be.at.most(10)
  hopp.expect(15).to.be.at.least(10)
})

hopp.test('Chai numbers - .within() range', () => {
  hopp.expect(7).to.be.within(5, 10)
  hopp.expect(5).to.be.within(5, 10)
  hopp.expect(10).to.be.within(5, 10)
})

hopp.test('Chai numbers - .closeTo() with delta', () => {
  hopp.expect(10).to.be.closeTo(10.5, 0.6)
  hopp.expect(9.99).to.be.closeTo(10, 0.1)
})

// PROPERTY ASSERTIONS
hopp.test('Chai properties - .property() checks', () => {
  const obj = { name: 'test', nested: { value: 42 } }
  hopp.expect(obj).to.have.property('name')
  hopp.expect(obj).to.have.property('name', 'test')
  hopp.expect(obj).to.have.nested.property('nested.value', 42)
})

hopp.test('Chai properties - .ownProperty() checks', () => {
  const obj = { own: 'value' }
  hopp.expect(obj).to.have.ownProperty('own')
  hopp.expect(obj).to.not.have.ownProperty('toString')
})

// LENGTH ASSERTIONS
hopp.test('Chai length - .lengthOf() for arrays and strings', () => {
  hopp.expect([1, 2, 3]).to.have.lengthOf(3)
  hopp.expect('hello').to.have.lengthOf(5)
  hopp.expect([]).to.have.lengthOf(0)
})

// COLLECTION ASSERTIONS
hopp.test('Chai collections - .keys() assertions', () => {
  const obj = { a: 1, b: 2, c: 3 }
  hopp.expect(obj).to.have.keys('a', 'b', 'c')
  hopp.expect(obj).to.have.all.keys('a', 'b', 'c')
  hopp.expect(obj).to.have.any.keys('a', 'd')
})

hopp.test('Chai collections - .members() for arrays', () => {
  hopp.expect([1, 2, 3]).to.have.members([3, 2, 1])
  hopp.expect([1, 2, 3]).to.include.members([1, 2])
})

hopp.test('Chai collections - .deep.members() for object arrays', () => {
  hopp.expect([{ a: 1 }, { b: 2 }]).to.have.deep.members([{ b: 2 }, { a: 1 }])
})

hopp.test('Chai collections - .oneOf() checks', () => {
  hopp.expect(2).to.be.oneOf([1, 2, 3])
  hopp.expect('a').to.be.oneOf(['a', 'b', 'c'])
})

// INCLUSION ASSERTIONS
hopp.test('Chai inclusion - .include() for arrays and strings', () => {
  hopp.expect([1, 2, 3]).to.include(2)
  hopp.expect('hello world').to.include('world')
})

hopp.test('Chai inclusion - .deep.include() for objects', () => {
  hopp.expect([{ a: 1 }, { b: 2 }]).to.deep.include({ a: 1 })
})

// FUNCTION/ERROR ASSERTIONS
hopp.test('Chai functions - .throw() assertions', () => {
  const throwFn = () => { throw new Error('test error') }
  const noThrow = () => { return 42 }

  hopp.expect(throwFn).to.throw()
  hopp.expect(throwFn).to.throw(Error)
  hopp.expect(throwFn).to.throw('test error')
  hopp.expect(noThrow).to.not.throw()
})

hopp.test('Chai functions - .respondTo() method checks', () => {
  const obj = { method: function() {} }
  hopp.expect(obj).to.respondTo('method')
  hopp.expect([]).to.respondTo('push')
})

hopp.test('Chai functions - .satisfy() custom matcher', () => {
  hopp.expect(10).to.satisfy((num) => num > 5)
  hopp.expect('hello').to.satisfy((str) => str.length === 5)
})

// OBJECT STATE ASSERTIONS
hopp.test('Chai object state - .sealed, .frozen, .extensible', () => {
  const sealed = Object.seal({ a: 1 })
  const frozen = Object.freeze({ b: 2 })
  const extensible = { c: 3 }

  hopp.expect(sealed).to.be.sealed
  hopp.expect(frozen).to.be.frozen
  hopp.expect(extensible).to.be.extensible
})

hopp.test('Chai number state - .finite', () => {
  hopp.expect(42).to.be.finite
  hopp.expect(Infinity).to.not.be.finite
})

// EXOTIC OBJECTS
hopp.test('Chai exotic - Set assertions', () => {
  const mySet = new Set([1, 2, 3])
  hopp.expect(mySet).to.be.instanceof(Set)
  hopp.expect(mySet).to.have.lengthOf(3)
})

hopp.test('Chai exotic - Map assertions', () => {
  const myMap = new Map([['key', 'value']])
  hopp.expect(myMap).to.be.instanceof(Map)
  hopp.expect(myMap).to.have.lengthOf(1)
})

// SIDE-EFFECT ASSERTIONS
hopp.test('Chai side-effects - .change() assertions', () => {
  const obj = { count: 0 }
  const changeFn = () => { obj.count = 5 }
  hopp.expect(changeFn).to.change(obj, 'count')

  const noChangeFn = () => {}  
  hopp.expect(noChangeFn).to.not.change(obj, 'count')
})

hopp.test('Chai side-effects - .change().by() delta', () => {
  const obj = { count: 10 }
