pm.test('pm.response.responseSize matches actual body length', () => {
  const bodyText = pm.response.text()
  // Use the same workaround as pm.response.responseSize for QuickJS
  const encoder = new TextEncoder()
  const encoded = encoder.encode(bodyText)
  // QuickJS represents Uint8Array as object with numeric keys
  const actualSize = encoded && typeof encoded.length === 'number' && encoded.length > 0
    ? encoded.length
    : Object.keys(encoded).filter(k => !isNaN(k)).length
  pm.expect(pm.response.responseSize).to.equal(actualSize)
})

