export {};
// Test PM namespace mutability - URL string assignment
pm.request.url = 'https://echo.hoppscotch.io/mutated-via-string'

// Test method mutation
pm.request.method = 'POST'

// Test header mutations
pm.request.headers.add({ key: 'Added-Header', value: 'added-value' })
pm.request.headers.upsert({ key: 'Original-Header', value: 'mutated-value' })

// Test body mutation via update()
pm.request.body.update({
  mode: 'raw',
  raw: JSON.stringify({ pmMutated: true, timestamp: Date.now() }),
  options: { raw: { language: 'json' } }
})

// Test auth mutation
pm.request.auth = {
  authType: 'bearer',
  authActive: true,
  token: 'pm-bearer-token-123'
}

