  pm.expect(pm.request.headers.has('Original-Header')).to.be.true
  pm.expect(pm.request.headers.get('Original-Header')).to.equal('mutated-value')
  pm.expect(pm.request.headers.get('Original-Header')).to.not.equal('original')
})

