export {};

pm.test('query.find() - finds param by string key', () => {
  const limitParam = pm.request.url.query.find('limit')
  if (limitParam) {
    pm.expect(limitParam).to.be.an('object')
    pm.expect(limitParam.key).to.equal('limit')
  } else {
    pm.expect(pm.request.url.query.has('limit')).to.be.true
  }
})

pm.test('query.find() - finds param by predicate function', () => {
  const limitParam = pm.request.url.query.find((p) => p && p.key === 'limit')
  if (limitParam) {
    pm.expect(limitParam).to.be.an('object')
    pm.expect(limitParam.value).to.equal('10')
  } else {
    pm.expect(pm.request.url.query.get('limit')).to.equal('10')
  }
})

pm.test('query.find() - returns null when not found', () => {
  const result = pm.request.url.query.find('nonexistent')
  pm.expect(result).to.be.null
})

pm.test('query.indexOf() - returns index for existing params', () => {
  // Verify indexOf works - check params that exist in actual URL
  const allParams = pm.request.url.query.all()
  const keys = Object.keys(allParams)
  if (keys.length > 0) {
    const firstKey = keys[0]
    const idx = pm.request.url.query.indexOf(firstKey)
    pm.expect(idx).to.be.a('number')
    pm.expect(idx).to.be.at.least(0)
  }
})

pm.test('query.indexOf() - returns index by object', () => {
  const allParams = pm.request.url.query.all()
  const keys = Object.keys(allParams)
  if (keys.length > 0) {
    const idx = pm.request.url.query.indexOf({ key: keys[0] })
    pm.expect(idx).to.be.a('number')
    pm.expect(idx).to.be.at.least(0)
  }
})

pm.test('query.indexOf() - returns -1 when not found', () => {
