  // Post-request sees actual sent URL, so we just verify params exist
  const allParams = pm.request.url.query.all()
  pm.expect(allParams).to.be.an('object')
  pm.expect(pm.request.url.query.has('limit')).to.be.true
  pm.expect(pm.request.url.query.has('offset')).to.be.true
