// Map & Set Assertions
pm.test('Map assertions - size property', () => {
  const map = new Map([['key1', 'value1'], ['key2', 'value2']])
  pm.expect(map).to.have.property('size', 2)
  pm.expect(map.size).to.equal(2)
})

pm.test('Set assertions - size property', () => {
  const set = new Set([1, 2, 3, 4])
  pm.expect(set).to.have.property('size', 4)
  pm.expect(set.size).to.equal(4)
})

