  pm.expect(set).to.be.instanceOf(Set)
  pm.expect(set).to.be.an.instanceOf(Set)
})

// Advanced Chai - closeTo
pm.test('closeTo - validates numbers within delta', () => {
  pm.expect(3.14159).to.be.closeTo(3.14, 0.01)
  pm.expect(10.5).to.be.closeTo(11, 1)
})

pm.test('closeTo - negation works', () => {
  pm.expect(100).to.not.be.closeTo(50, 10)
  pm.expect(3.14).to.not.be.closeTo(10, 0.1)
})

pm.test('approximately - alias for closeTo', () => {
  pm.expect(2.5).to.approximately(2.4, 0.2)
  pm.expect(99.99).to.approximately(100, 0.1)
})

// Advanced Chai - finite
pm.test('finite - validates finite numbers', () => {
  pm.expect(123).to.be.finite
  pm.expect(0).to.be.finite
  pm.expect(-456).to.be.finite
})

pm.test('finite - negation for Infinity', () => {
  pm.expect(Infinity).to.not.be.finite
  pm.expect(-Infinity).to.not.be.finite
  pm.expect(NaN).to.not.be.finite
})

