requests.testScript:
export {};

// ========================================
// TYPE PRESERVATION TESTS (CLI Compatible)
// ========================================

// IMPORTANT NOTE: Type preservation works perfectly WITHIN script execution scope
// Values persisted across request boundaries (pre-request → test) may be serialized
// This is expected CLI behavior for environment persistence/display

// Test values set from pre-request
pm.test('string values work across scripts', () => {
  pm.expect(pm.environment.get('string_value')).to.equal('hello')
})

// ========================================
// TYPE PRESERVATION WITHIN SINGLE SCRIPT
// (This is where type preservation really shines!)
// ========================================

pm.test('numbers are preserved as numbers (same script)', () => {
  pm.environment.set('num', 42)
  const value = pm.environment.get('num')
  pm.expect(value).to.equal(42)
  pm.expect(typeof value).to.equal('number')
})

pm.test('booleans are preserved as booleans (same script)', () => {
  pm.environment.set('bool_true', true)
  pm.environment.set('bool_false', false)
  pm.expect(pm.environment.get('bool_true')).to.equal(true)
  pm.expect(pm.environment.get('bool_false')).to.equal(false)
  pm.expect(typeof pm.environment.get('bool_true')).to.equal('boolean')
})

pm.test('null is preserved as actual null (same script)', () => {
  pm.environment.set('null_val', null)
  const value = pm.environment.get('null_val')
  pm.expect(value).to.equal(null)
  pm.expect(value === null).to.be.true
  pm.expect(typeof value).to.equal('object')
})

pm.test('undefined is preserved as actual undefined (same script)', () => {
  pm.environment.set('undef_val', undefined)
  const value = pm.environment.get('undef_val')
  pm.expect(value).to.equal(undefined)
  pm.expect(typeof value).to.equal('undefined')
  pm.expect(pm.environment.has('undef_val')).to.be.true
})

pm.test('arrays are preserved with direct access', () => {
  pm.environment.set('arr', [1, 2, 3])
  const value = pm.environment.get('arr')

  pm.expect(Array.isArray(value)).to.be.true
  pm.expect(value.length).to.equal(3)
  pm.expect(value[0]).to.equal(1)
  pm.expect(value[2]).to.equal(3)
})

pm.test('single-element arrays remain arrays', () => {
  pm.environment.set('single', [42])
  const value = pm.environment.get('single')

  pm.expect(Array.isArray(value)).to.be.true
  pm.expect(value.length).to.equal(1)
  pm.expect(value[0]).to.equal(42)
})

pm.test('empty arrays are preserved', () => {
  pm.environment.set('empty_arr', [])
  const value = pm.environment.get('empty_arr')

  pm.expect(Array.isArray(value)).to.be.true
  pm.expect(value.length).to.equal(0)
