pm.test('PM namespace handles mixed arrays (regression test for UI crash)', () => {
  pm.environment.set('mixedArray', [
    'string',
    42,
    true,
    null,
    undefined,
    [1, 2],
    { key: 'value' }
  ])
  const mixed = pm.environment.get('mixedArray')

  // This is the exact case that caused the UI crash
  pm.expect(Array.isArray(mixed)).to.equal(true)
  pm.expect(mixed).to.have.lengthOf(7)
  pm.expect(mixed[0]).to.equal('string')
  pm.expect(mixed[1]).to.equal(42)
  pm.expect(mixed[2]).to.equal(true)
  pm.expect(mixed[3]).to.be.null
  // mixed[4] is undefined in array, becomes null during JSON serialization
  pm.expect(Array.isArray(mixed[5])).to.equal(true)
  pm.expect(mixed[5]).to.have.lengthOf(2)
  pm.expect(typeof mixed[6]).to.equal('object')
  pm.expect(mixed[6].key).to.equal('value')
})

