  pm.environment.set('testObj', { foo: 'bar', num: 42 })

  const arr = pm.environment.get('testArr')
  const obj = pm.environment.get('testObj')

  // Should work like Postman: runtime types preserved
  pm.expect(arr.length).to.equal(3)
  pm.expect(obj.foo).to.equal('bar')

  // Verify no String() coercion happened
  pm.expect(arr).to.not.equal('1,2,3')
