})

pm.test('Type preservation: UI compatibility regression test', () => {
  // This test validates the fix for the reported bug:
  // "TypeError: a.match is not a function at details.vue:387:10"

  pm.environment.set('mixedTest', [
    'string', 42, true, null, undefined, [1, 2], { key: 'value' }
  ])

  const mixed = pm.environment.get('mixedTest')

  // Should NOT throw any errors
  let errorCount = 0
  try {
    // Access all elements
    mixed.forEach(item => {
      // Should work with all types
      const type = typeof item
      const validTypes = ['string', 'number', 'boolean', 'object']
      if (!validTypes.includes(type)) {
        errorCount++
      }
    })
  } catch (e) {
    errorCount++
  }

  pm.expect(errorCount).to.equal(0)
})
